import './Advantages.css'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee } from '@fortawesome/free-solid-svg-icons'

import { Col, Container, Row } from "react-bootstrap";

function Advantages(){

    const ArraysOfAdvantage = [{
        id: 1,
        title: "Гарантия",
        text: "Гарантия полета шаров от 7 до 30 дней"
    },
    {
        id: 2,
        title: "Качество",
        text: "Только сертифицированные производители"
    },
    {
        id: 3,
        title: "Доставка",
        text: "Оперативная доставка по Алматинской области"
    },
    {
        id: 4,
        title: "Оплата",
        text: "Оплата любым удобным способам"
    }];
    
    const ArraysOfAdvantageOnMap = ArraysOfAdvantage.map((ArrayOfAdvantage) =>
    <Col key = {ArrayOfAdvantage.id} className='text-center' lg = "3" md = "6" sm = "12">
        <h3>{ArrayOfAdvantage.title}</h3>
        <p>{ArrayOfAdvantage.text}</p>
    </Col>
    )

    return(
        <Container className="padding">
            <h1>Преимущества</h1>

            <Row className="padding">
                {ArraysOfAdvantageOnMap}
            </Row>
        </Container>
    )
}

export default Advantages
