import './swipertest.css';

import { Container, Row, Col } from "react-bootstrap";

import Image1 from '../SwiperTest/Image/image1.jpg';
import Image2 from '../SwiperTest/Image/image2.jpg';

function SwiperTest(){

    const mainElement = document.documentElement;

    function ScrollTop(){
        window.scrollBy(0, 50);
        const ScrollFromTop = window.pageYOffset;
        alert(ScrollFromTop)
    }

    const ArraysOfSwiper = [{
        id: 1,
        image: Image1,
        title: 'Land Deep Male Two Living Give Fruit',
        text: 'Aug 02, 2021'
    },
    {
        id: 2,
        image: Image2,
        title: 'Land Deep Male Two Living Give Fruit',
        text: 'Aug 02, 2021'
    },
    {
        id: 3,
        image: Image2,
        title: 'Land Deep Male Two Living Give Fruit',
        text: 'Aug 02, 2021'
    }];
    const ArrayOfSwiperOnMap = ArraysOfSwiper.map((ArrayOfSwiper) =>

    <Col key = {ArrayOfSwiper.id} lg = "4" md = "6" sm = "12">
        <div className = "swiper-wrapper">
            <img src = {ArrayOfSwiper.image} className = "w-100 h-100 swiper-image"/>
            
            <div className = "swiper-content">
                <h5>{ArrayOfSwiper.title}</h5>
                <p>{ArrayOfSwiper.text}</p>

                <button className = "btn btn-primary" onClick={ScrollTop}>
                    On 50px
                </button>
            </div>
        </div>
    </Col>
    )

    return(
        <Container fluid>
            <div className = "swiper">
                <Row className = "g-2">
                    {ArrayOfSwiperOnMap}
                </Row>   
            </div>
        </Container>     
    )
}
export default SwiperTest
