import React from "react";
import './Helper.css';


function Helper(){
const [number, setNumber] = React.useState(0);
const [cost, setCost] = React.useState(0);

function OnIncrease(){
    setNumber(number + 1);
    setCost(cost + 500)
}

function OnDecrease(){
    setNumber(number - 1);
    setCost(cost - 500);
}

    return(
        <div className="block">
            <div className="number">
                <input className="input" value = {number} /> <br/>
                Цена: {cost}тг
            </div>

            <div>
                <p onClick={OnIncrease} className="button">+</p>
                <p onClick={OnDecrease} className="button">-</p>
            </div>
        </div>
    )
}

export default Helper
