import React from "react";
import './Helper.css';
import { Col, Container, Row } from "react-bootstrap";


function Helper(){

    const [count, setCount] = React.useState(0);
    const [cost, setCost] = React.useState(0);

    function Increase(){
        setCount(count + 1)
        setCost(cost + 49.99)
    }
    function Decrease(){
        setCount(count - 1)
        setCost(cost - 49.99)
    }

    return(
        <Container>
            <Row>
                <Col md = "6" sm = "12">
                    <img src = "https://ae04.alicdn.com/kf/S0a6b99eb60904b5a8b3299d7afc16df2i.jpg_640x640.jpg" className="w-100" />
                </Col>

                <Col md = "3" sm = "12">
                    <h4 className="text-muted">Text</h4>
                    <h1></h1>
                </Col>

                <Col md = "3" sm = "12">
                    <h3>1 257,60 ₽</h3>
                    <h4 className="text-muted">4 192,00 ₽ <span style={{'color': 'red'}}>-70%</span></h4>
                </Col>
            </Row>
        </Container>
    )
}

export default Helper
