import './Blog.css'

import { Col, Row } from "react-bootstrap";

import Blog1 from '../MorianImage/blog1.webp';
import Blog2 from '../MorianImage/blog2.webp';
import Blog3 from '../MorianImage/blog3.jpg';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleDoubleRight } from '@fortawesome/free-solid-svg-icons';

function Blog(){

    const ArraysOfBlog = [{
        id: 1,
        image: Blog1,
        title: 'The Art Of Ceramic Pigments For Digital Inks',
    },
    {
        id: 2,
        image: Blog2,
        title: 'The Beating Heart Of The Ceramic Sector',
    },
    {
        id: 3,
        image: Blog3,
        title: 'Ceramic Glazes: The Chemical Component!',
    },];
    const ArraysOfBlogOnMap = ArraysOfBlog.map((ArrayOfBlog) => 
    <Col xl = "4" lg = "6" md = "12" key = {ArrayOfBlog.id} className = "blog__card">
        <div className = "blog__image">
            <img src = {ArrayOfBlog.image} className = "w-100 h-100"/>
            <span className = "blog__date">
                22 Jul
            </span>
        </div>

        <div class = "blog__content">
            <h4>News</h4>
            <h3>{ArrayOfBlog.title}</h3>
            <p>Our lifestyle habits have been revolutionized by the Covid19 pandemic. Our daily life has been absolutely conditioned: our home and its interiors have become even more important, more than they were previously....</p>
            <a href = "" className = "blog__link">Read More <FontAwesomeIcon icon={faAngleDoubleRight} /></a>
        </div>
    </Col>
    )

    return(
        <div>
            <div class = "blog">
                <h3 className = "blog__head">Our Blogs</h3>
                <div className = "line"></div>

                <Row className = "g-1">
                    {ArraysOfBlogOnMap}
                </Row>
            </div>
        </div>
    )
}

export default Blog
