import { useEffect, useState } from "react";
import { Col, Container, Row } from "react-bootstrap";

function Facts(){
    
    const [years, setYears] = useState(0);
    const limitYears = 5; 
    
    const [count, setCount] = useState(0);
    const limitCount = 500;

    const [delivery, setDelivery] = useState(0);
    const limitDelivery = 30;
    
    useEffect(() => {
        const interval1 = setInterval(() => {
            if (count < limitYears) {
                setYears(count + 1);
            } 
            else {
                clearInterval(interval1);
            }
        }, 500);
        
        return () => {
            clearInterval(interval1);
        };
    }, [years]
    );

    useEffect(() => {
        const interval2 = setInterval(() => {
            if (count < limitCount) {
                setCount(count + 1);
            } 
            else {
                clearInterval(interval2);
            }
        }, 10);
        
        return () => {
            clearInterval(interval2);
        };
    }, [count]
    );

    useEffect(() => {
        const interval3 = setInterval(() => {
            if (count < limitDelivery) {
                setDelivery(count + 1);
            } 
            else {
                clearInterval(interval3);
            }
        }, 200);
        
        return () => {
            clearInterval(interval3);
        };
    }, [delivery]
    );

    return(
        <Container>

            <h1 className="text-center">Факты о нашей компании</h1>

            <Row>
                <Col className="text-center" lg = "4" md = "6" sm = "12">
                    <h1>{years}</h1>
                    <p>Более 9 лет на рынке, начиная с 2012 года</p>
                </Col>

                <Col className="text-center" lg = "4" md = "6" sm = "12">
                    <h1>{count}</h1>
                    <p>Более 500 шаров качаем ежедневно</p>
                </Col>

                <Col className="text-center" lg = "4" md = "6" sm = "12">
                    <h1>{delivery}</h1>
                    <p>Быстрая доставка от 40 минут</p>
                </Col>
            </Row>
        </Container>
    )
}

export default Facts
