import { Button, Col, Container, Row } from "react-bootstrap"

function ArrayForCatalog(){

    const [show, setShow] = useState(false);
    
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const ArraysForCatalogImages = [{
        id: 1,
        title: "Lorem ipsum dolor",
        text: "Lorem ipsum dolor",
        button: "Нажми на меня",
        onClick: ''
    }]
    const ArraysForCatalogImagesOnMap = ArraysForCatalogImages.map((ArrayForCatalogImages)=>
    <div>
        <Col key = {ArrayForCatalogImages.id} lg = "4" md = "6" sm = "12">
            <div className = "container">
                <img src = "" className = "w-100"/>

                <h1>{ArrayForCatalogImages.title}</h1>
                <p>{ArrayForCatalogImages.text}</p>

                <Button variant = "dark" onClick={handleShow}>{ArrayForCatalogImages.button}</Button>
            </div>
        </Col>
    </div>
    )

    return(
        <Container>
            <Row>
                {ArraysForCatalogImagesOnMap}
            </Row>
        </Container>
    )
}

export default ArrayForCatalog
