import { useState } from 'react';
import React from 'react';
import './Catalog.css';

import { Button, Card, Col, Container, Modal, Row } from "react-bootstrap";

function Catalog(){
    const ArraysOfCollection = [
        {
            id: 1,
            image: '',
            title: 'Шары без рисунка',
            text: 'Гелиевые шары обрабатываются специальным жидкостью HiFloat для продолжительного полета (минимум 3-7 дней). Имеется большая палитра цветов. Качественные и экологически чистые материалы, безопасные для детей. Не имеют неприятного запаха.',
            name: 'Шары воздушные',
            cost: 2999
        },
        {
            id: 2,
            image: '',
            title: 'Hello world2',
            text: '2Lorem ipsum dolor sit, amet consectetur adipisicing elit. Aliquam, iusto consequuntur eos voluptatem laboriosam esse? Quaerat vel sunt natus possimus?',
            name: 'Шары воздушные',
            cost: 2999,
            notcost: 3499,
            size: '12 дюйм - 30см',
            color: 'Все цвета',
            material: 'Латекс',
            product: 'Sempertex'
        }
    ];
    
    const [showModal, setShowModal] = useState(ArraysOfCollection.map(() => false));
    
    const handleClose = (index) => {
        const updatedShowModal = [...showModal];
        updatedShowModal[index] = false;
        setShowModal(updatedShowModal);
    };

    const handleShow = (index) => {
        const updatedShowModal = [...showModal];
        updatedShowModal[index] = true;
        setShowModal(updatedShowModal);
    };

    const ArraysOfCollectionOnMap = ArraysOfCollection.map((ArrayOfCollection, index) => (
        <React.Fragment key={ArrayOfCollection.id}>
            <Col lg="4" md="6" sm="12">
                <Card>
                    <img className="w-100" onClick={() => handleShow(index)} src={ArrayOfCollection.image}/>

                    <Card.Header>
                        <Card.Title onClick={() => handleShow(index)}><h3>{ArrayOfCollection.title}</h3></Card.Title>
                    </Card.Header>

                    <Card.Body>
                        <div className="d-flex justify-content-between align-items-center">
                            <div>
                                <h4>{ArrayOfCollection.name}</h4>
                            </div>

                            <div>
                                <h5>{ArrayOfCollection.cost}тг</h5>
                            </div>
                        </div>
                    </Card.Body>
                </Card>
            </Col>

            <Modal size = "xl" show={showModal[index]} onHide={() => handleClose(index)}>
                <Row>
                    <Col lg = "4" md = "6" sm = "12">
                        <img className='w-100' src = {ArrayOfCollection.image}/>
                    </Col>

                    <Col lg = "8" md = "6" sm = "12">
                        <Modal.Body>
                            <h3>{ArrayOfCollection.title}</h3>
                            <hr/>
                            <span className='cost'>{ArrayOfCollection.cost}тг</span> <span className='notcost'><s>{ArrayOfCollection.notcost}тг</s></span>
                            <hr/>

                            <div className = "modalProperty">
                                <h6>Размер:</h6> <p>{ArrayOfCollection.size}</p>
                                <h6>Цвет:</h6> <p>{ArrayOfCollection.color}</p>
                                <h6>Материал:</h6> <p>{ArrayOfCollection.material}</p>
                                <h6>Производительность:</h6> <p>{ArrayOfCollection.product}</p> 
                            </div>

                            <p>{ArrayOfCollection.text}</p>
                        </Modal.Body>
                    </Col>
                </Row>
            </Modal>
        </React.Fragment>
    ));


    return(
        <Container className = "padding">

            <h1 id = "catalog">Каталог Шаров</h1>

            <Row className='g-2'>
                {ArraysOfCollectionOnMap}
            </Row>
        </Container>
    )
}

export default Catalog;
