import React from "react";
import './Helper.css';
import { Card, Col, Container, Row } from "react-bootstrap";


function Helper(){

    const [count, setCount] = React.useState(0);

    function Increase(){
        setCount(count + 1)
    }
    function Decrease(){
        setCount(count - 1)
    }

    return(
        <Container>
            <Row className = "d-flex align-items-center">
                <Col lg = "4" md = "6" sm = "12">
                    <Card>
                        <img src = "https://static.nike.com/a/images/t_PDP_1728_v1/f_auto,q_auto:eco/758c805c-dde5-4df5-9780-56196bcc238a/air-jordan-1-retro-high-og-womens-shoes-LcJVSj.png" className = "w-100" />
                        
                        <Card.Body>
                            <Card.Title>
                                <h4>Air Jordan 1 Retro High OG</h4>
                            </Card.Title>

                            <Card.Text>
                                <p>Game on! Inspired by MJ's high school days, this energetic colorway brings A+ energy to the streets. Bold bursts of Game Royal and Varsity Maize let you join the team in style, while subtly weathered leather ups the vintage appeal.</p>
                            </Card.Text>

                            <div class = "d-flex justify-content-center">
                                <p type = "button" className = "btn btn-primary" onClick = {Increase}>+</p>
                                <input value = {count} />
                                <p type = "button" className = "btn btn-primary" onClick = {Decrease}>-</p>
                            </div>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    )
}

export default Helper
