import './SwiperExample.css';

import { Col, Container, Row } from "react-bootstrap";
import { Swiper, SwiperSlide } from 'swiper/react';

import Image1 from '../Swiper/Image/image1.webp';
import Image2 from '../Swiper/Image/image2.webp';
import Image3 from '../Swiper/Image/image3.webp';

import 'swiper/swiper.min.css';
import 'swiper/components/navigation/navigation.min.css';
import 'swiper/components/pagination/pagination.min.css';
import 'swiper/components/scrollbar/scrollbar.min.css';

import { Navigation, Pagination, Scrollbar, A11y } from 'swiper';

function SwiperExample(){
    return(
        <Container fluid>
            <Swiper
            modules={[Navigation, Pagination, Scrollbar, A11y]}
            spaceBetween={50}
            slidesPerView={3}
            navigation
            pagination={{ clickable: true }}
            scrollbar={{ draggable: true }}
            onSwiper={(swiper) => console.log(swiper)}>
                
                <SwiperSlide>
                    <img src = {Image1} className = "w-100"/>
                </SwiperSlide>

                <SwiperSlide>
                    <img src = {Image2} className = "w-100"/>
                </SwiperSlide>

                <SwiperSlide>
                    <img src = {Image3} className = "w-100"/>
                </SwiperSlide>
            </Swiper>
        </Container>
    )
}

export default SwiperExample
