import './swipertest.css';

import { Container, Row, Col } from "react-bootstrap";

import Image1 from '../SwiperTest/Image/image1.jpg';
import Image2 from '../SwiperTest/Image/image2.jpg';
import { useState } from 'react';

function SwiperTest(){

    const [cost, setCost] = useState(0);

    const OnePlus = () =>{
        setCost(cost + 1);
    }
    const OneMinus = () =>{
        setCost(cost - 1);
    }

    const ArraysOfSwiper = [{
        id: 1,
        image: Image1,
        title: 'Land Deep Male Two Living Give Fruit',
        text: 'Aug 02, 2021'
    },
    {
        id: 2,
        image: Image2,
        title: 'Land Deep Male Two Living Give Fruit',
        text: 'Aug 02, 2021'
    },
    {
        id: 3,
        image: Image1,
        title: 'Land Deep Male Two Living Give Fruit',
        text: 'Aug 02, 2021'
    }];
    const ArrayOfSwiperOnMap = ArraysOfSwiper.map((ArrayOfSwiper) =>

    <Col key = {ArrayOfSwiper.id} lg = "4" md = "6" sm = "12">
        <div className = "swiper-wrapper">
            <img src = {ArrayOfSwiper.image} className = "w-100 swiper-image"/>
            
            <div className = "swiper-content">ь
                <h5>{ArrayOfSwiper.title}</h5>
                <p>{ArrayOfSwiper.text}</p>
                <button className = "button" onClick={OnePlus}>Добавить в корзину</button>
                <button className = "button" onClick={OneMinus}>Убрать с корзины</button>
            </div>
        </div>
    </Col>
    )

    return(
        <Container fluid>
            <div className = "swiper">
                <Row className = "g-2">
                    {ArrayOfSwiperOnMap}
                </Row> 

                <h1>Сумма: {cost}</h1>  
            </div>
        </Container>     
    )
}
export default SwiperTest
