import { useState } from 'react';
import React from 'react';

import './Catalog.css';

import { Button, Col, Container, Modal, Row } from "react-bootstrap";

import OneOfFour from './CatalogImages/OneOfFour.jpeg';
import TwoOfFour from './CatalogImages/TwoOfFour.jpeg';
import ThreeOfFour from './CatalogImages/ThreeOfFour.webp';
import FourOfFour from './CatalogImages/FourOfFour.jpeg';

function Catalog(){
    const ArraysOfCollection = [
        {
            id: 1,
            image: '',
            title: 'Шары без Рисунка',
            text: '2Lorem ipsum dolor sit, amet consectetur adipisicing elit. Aliquam, iusto consequuntur eos voluptatem laboriosam esse? Quaerat vel sunt natus possimus?',
            cost: 2499,
            notcost: 2999,
            size: '12 дюйм - 30см',
            color: 'Все цвета',
            material: 'Латекс',
            product: 'Sempertex (Колумбия)'
        },
        {
            id: 2,
            image: '',
            title: 'Фольгированные звездочки',
            text: 'Что делать, если бумажная «валентинка» вас не устраивает? Как признаться в любви эффектно и без слов? Чем украсить помещение, в котором будет праздноваться свадьба?Воздушные шары в виде сердца – универсальный декор, который подходит для всех романтических и знаковых событий в жизни любящих людей. Их можно дарить в качестве сувенира и использовать как оформление. Они прекрасно выглядят, дают широкие возможности для творчества, а стоят при этом копейки.',
            cost: 2999,
            notcost: 3499,
            size: '12 дюйм - 30см',
            color: 'Красный, белый, розовый, оранжевый, зеленый',
            material: 'Латекс',
            product: 'Sempertex'
        },
        {
            id: 3,
            image: '',
            title: 'Фольгированные цифры',
            text: 'Замечательный праздник День Рождения только раз в году! Поэтому отпраздновать его необходимо как следует! Думаете, как ярко и красочно поздравить именинника? Тогда Вам нужно прямо сейчас заказать шары на День Рождения именно у нас! Наша команда профессиональных оформителей поможет Вам воплотить любую идею в жизнь. Украшение для праздника в виде воздушных шаров – это самый быстрый, удобный и практичный вариант, чтобы недорого и эффектно создать праздничное настроение детям и взрослым!',
            cost: 2999,
            notcost: 3499,
            size: '12 дюйм - 30см',
            color: 'Все цвета',
            material: 'Латекс',
            product: 'Sempertex'
        },
        {
            id: 4,
            image: '',
            title: 'Фольгированные цифры',
            text: 'Замечательный праздник День Рождения только раз в году! Поэтому отпраздновать его необходимо как следует! Думаете, как ярко и красочно поздравить именинника? Тогда Вам нужно прямо сейчас заказать шары на День Рождения именно у нас! Наша команда профессиональных оформителей поможет Вам воплотить любую идею в жизнь. Украшение для праздника в виде воздушных шаров – это самый быстрый, удобный и практичный вариант, чтобы недорого и эффектно создать праздничное настроение детям и взрослым!',
            cost: 2999,
            notcost: 3499,
            size: '12 дюйм - 30см',
            color: 'Все цвета',
            material: 'Латекс',
            product: 'Sempertex'
        }
    ];
    
    const [showModal, setShowModal] = useState(ArraysOfCollection.map(() => false));
    
    const handleClose = (index) => {
        const updatedShowModal = [...showModal];
        updatedShowModal[index] = false;
        setShowModal(updatedShowModal);
    };

    const handleShow = (index) => {
        const updatedShowModal = [...showModal];
        updatedShowModal[index] = true;
        setShowModal(updatedShowModal);
    };

    const ArraysOfCollectionOnMap = ArraysOfCollection.map((ArrayOfCollection, index) => (
        <React.Fragment key={ArrayOfCollection.id}>
            <Col lg="4" md="6" sm="12">
                <div className = "catalog">
                    <img className="w-100" src={ArrayOfCollection.image}/>

                    <div className='catalogContent'>
                        <div className='catalogWord'>
                            <p className = "catalogTitle" onClick={() => handleShow(index)} style={{fontSize: '24px'}}>{ArrayOfCollection.title}</p>
                        </div>

                        <div className='catalogButton'>
                            <Button variant='dark'>Заказать</Button>
                        </div>
                    </div>
                </div>
            </Col>

            <Modal size = "xl" show={showModal[index]} onHide={() => handleClose(index)}>
                <div className = 'modalPadding'>
                <Row>
                    <Col lg = "4" md = "6" sm = "12">
                        <img className='w-100' src = {ArrayOfCollection.image}/>
                    </Col>

                    <Col lg = "8" md = "6" sm = "12">
                        <Modal.Body>
                            <h3>{ArrayOfCollection.title}</h3>
                            <hr/>
                            <span className='cost'>{ArrayOfCollection.cost}тг</span> <span className='notcost'><s>{ArrayOfCollection.notcost}тг</s></span>
                             <hr/>

                            <div className = "modalProperty">
                                <h6>Размер: {ArrayOfCollection.size}</h6>
                                <h6>Цвет: {ArrayOfCollection.color}</h6>
                                <h6>Материал: {ArrayOfCollection.material}</h6>
                                <h6>Производительность: {ArrayOfCollection.product}</h6>
                            </div>

                            <p>{ArrayOfCollection.text}</p>
                        </Modal.Body>
                    </Col>
                </Row>
                </div>
            </Modal>
        </React.Fragment>
    ));


    return(
        <Container className = "padding">

            <h1 id = "catalog">Каталог Шаров</h1>

            <Row>
                {ArraysOfCollectionOnMap}
            </Row>
        </Container>
    )
}

export default Catalog;
