import React from 'react';

import './ToDoList.css';

import { Button, Container, Form, InputGroup } from 'react-bootstrap'

function ToDoList(){

    const[InputValue, SetInputValue] = React.useState("");
    const[todos, setTodos] = React.useState([]);

    const handleInputText = (event) => {
        SetInputValue(event.target.value);
    }

    const AddInputText = (event) =>{
        if (InputValue !== '') {
            const newTodo = {
                id: new Date().getTime(),
                text: InputValue,
            };
            setTodos([...todos, newTodo]);
            setInputValue('');
          }
        }

    const DeleteInputText = (id) =>{
        const updatedTodos = todos.filter((todo) => todo.id !== id);
        setTodos(updatedTodos);
    }

    return(
        <Container className = "text-center">
            <div className = "block">
                <div className='title'>
                    <h1>Title of ToDoList</h1>
                </div>

                <InputGroup>
                <InputGroup.Text>#</InputGroup.Text>
                <Form.Control placeholder='Add something to ToDoList' value = {InputValue} onChange={handleInputText}></Form.Control>
                <Button onClick={AddInputText} variant = "outline-secondary">Button</Button>
                <ul>
                    {todos.map((todo) => (
                    <li key={todo.id}>
                        {todo.text}
                        <button onClick={() => DeleteInputText(todo.id)}>Delete</button>
                    </li>
                    ))}

                </ul>
                </InputGroup>
            </div>
        </Container>
    )
}

export default ToDoList
