import { useState } from 'react';
import './Catalog.css';

import { Button, Card, Col, Container, Modal, Row } from "react-bootstrap";

function Catalog(){

    const [showModal, setShowModal] = useState(
        ArraysOfCollection.map(() => false)
      );
    
      const handleClose = (index) => {
        const updatedShowModal = [...showModal];
        updatedShowModal[index] = false;
        setShowModal(updatedShowModal);
      };
    
      const handleShow = (index) => {
        const updatedShowModal = [...showModal];
        updatedShowModal[index] = true;
        setShowModal(updatedShowModal);
      };

    const ArraysOfCollection = [{
        id: 1,
        image: '',
        title: 'Hello world',
        text: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit. Aliquam, iusto consequuntur eos voluptatem laboriosam esse? Quaerat vel sunt natus possimus?',
        name: 'Шары воздушные',
        cost: 2999
    },
    {
        id: 2,
        image: '',
        title: 'Hello world2',
        text: 'NeLorem ipsum dolor sit, amet consectetur adipisicing elit. Aliquam, iusto consequuntur eos voluptatem laboriosam esse? Quaerat vel sunt natus possimus?',
        name: 'Шары воздушные',
        cost: 2999
    }];

    const ArraysOfCollectionOnMap = ArraysOfCollection.map((ArrayOfCollection) =>
    <>
    <Col key = {ArrayOfCollection.id} lg = "4" md = "6" sm = "12">
        <Card>
            <img className="w-100 loop" onClick={handleShow} src = 'https://images.pexels.com/photos/16015539/pexels-photo-16015539/free-photo-of-2023.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'/>

            <Card.Header>
                <Card.Title className = "loop" onClick={handleShow}>{ArrayOfCollection.title}</Card.Title>
            </Card.Header>

            <Card.Body>
                <Card.Text>{ArrayOfCollection.text}</Card.Text>

                <div className="d-flex justify-content-between align-items-center">
                    <div>
                        <h4>{ArrayOfCollection.name}</h4>
                    </div>
                    
                    <div>
                        <h5>{ArrayOfCollection.cost}тг</h5>
                    </div>
                </div>
            </Card.Body>
        </Card>
    </Col>

    <Modal size = "lg" show={show} onHide={handleClose}>
        <Modal.Header closeButton>
            <Modal.Title>{ArrayOfCollection.title}</Modal.Title>
        </Modal.Header>
        
        <Modal.Body>{ArrayOfCollection.text}</Modal.Body>
    </Modal>
    </>
    )


    return(
        <Container className = "padding">

            <h1 id = "catalog">Каталог Шаров</h1>

            <Row className='g-2'>
                {ArraysOfCollectionOnMap}
            </Row>
        </Container>
    )
}

export default Catalog;
