import './Choice.css'

import heart from './ChoiceSVG/bag-heart.svg'
import check from './ChoiceSVG/bag-check.svg'
import arrow from './ChoiceSVG/bag-arrow.svg'

import { Col, Container, Row } from 'react-bootstrap'

function Choice(){

    const ArraysOfChoice = [{
        id: 1,
        title: 'Hello world',
        text: 'Lorem ipsum dolor',
        svg: {heart}
    },
    {
        id: 2,
        title: 'Hello world',
        text: 'Lorem ipsum dolor',
        svg: {check}
    },
    {
        id: 3,
        title: 'Hello world',
        text: 'Lorem ipsum dolor',
        svg: {arrow}
    }
    ]
    const ArraysOfChoiceOnMap = ArraysOfChoice.map((ArrayOfChoice) => 
    <Col key = {ArrayOfChoice.id} lg = "4" md = "6" sm = "12">

        <img src = {ArrayOfChoice.svg} className='w-100'/>
        
        <h1>{ArrayOfChoice.title}</h1>
        <p>{ArrayOfChoice.text}</p>
    </Col>
    )

    return(
        <Container fluid>
            <Container>
                <div>
                    <h1>Changing real estate for the better</h1>
                </div>

                <Row>
                    {ArraysOfChoiceOnMap}
                </Row>
            </Container>
        </Container>
    )
}

export default Choice
