import { Container } from 'react-bootstrap';
import './test.css';

import { useState } from 'react';

function Test(){
    const[number, setNumber] = useState(0);
    let Increase = () =>{
        setNumber(number + 1)
    }
    let Decrease = () =>{
        setNumber(number - 1)
    }

    return(
        <div>
            <Container fluid>
                <div className = "container">
                    <h1>Счетчик: {number}</h1>
                    <button className = "button" onClick={Increase}>Press Me To Increase Number</button>
                    <button className = "button" onClick={Decrease}>Press Me To Decrease Number</button>
                </div>
            </Container>
        </div>
    )
}

export default Test
