import './Product.css'

import { Col, Container, Row } from "react-bootstrap";

import Product1 from '../MorianImage/product1.webp';
import Product2 from '../MorianImage/product2.webp';
import Product3 from '../MorianImage/product3.webp';
import Product4 from '../MorianImage/product4.webp';
import Product5 from '../MorianImage/product5.webp';
import Product6 from '../MorianImage/product6.webp';
import Product7 from '../MorianImage/product7.webp';
import Product8 from '../MorianImage/product8.webp';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeart, faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';

function Product(){

    const ArraysOfProduct = [{
        id: 1,
        image: Product1,
        title: 'Pepper Drift Vase',
        cost: '$40.00',
        notcost: '$48.00'
    },
    {
        id: 2,
        image: Product2,
        title: 'Planter Vase',
        cost: '$76.00',
        notcost: '$87.00'
    },
    {
        id: 3,
        image: Product3,
        title: 'Minimalist Glass Mini',
        cost: '$26.00',
        notcost: ''
    },
    {
        id: 4,
        image: Product4,
        title: 'Simple White Ceramic',
        cost: '$32.00',
        notcost: ''
    },
    {
        id: 5,
        image: Product5,
        title: 'Plates Gallery Ceramics',
        cost: '$64.00',
        notcost: ''
    },
    {
        id: 6,
        image: Product6,
        title: 'Pebbleface 01 Print',
        cost: '$47.00',
        notcost: '$59.00'
    },
    {
        id: 7,
        image: Product7,
        title: 'Margaret Landscape Plate',
        cost: '$19.00',
        notcost: ''
    },
    {
        id: 8,
        image: Product8,
        title: 'Mekko Pendant Light',
        cost: '$250.00',
        notcost: '$319.00'
    }];
    const ArraysOfProductOnMap = ArraysOfProduct.map((ArrayOfProduct)=>
    <Col key = {ArrayOfProduct.id} xl = "3" lg = "4" md = "6" sm = "6">
        <div className = "product__image">
            <img src = {ArrayOfProduct.image} className = "w-100 h-100"/>
            <div className = "product__bottom">
                <span className = "product__heart" title = "Add to WishList"> 
                    <FontAwesomeIcon icon = {faHeart} />
                </span>
                <FontAwesomeIcon icon = {faMagnifyingGlass} />
            </div>
        </div>

        <div className = "product__content">
            <h4>
                <a href = "" className = "product__title">{ArrayOfProduct.title}</a>
            </h4>
            <p className = "product__cost">
                <span>{ArrayOfProduct.cost}</span>
                <s>{ArrayOfProduct.notcost}</s>
            </p>
        </div>
    </Col>
    )

    return(
        <div className = "product">
            <Container fluid>
                <h3 className = "product__head">Featured Product</h3>
                <div class = "line"></div>

                <div className = "product__buttons">
                    <button className = "dark__button">New Arrivals</button>
                    <button className = "dark__button">Best Sellers</button>
                    <button className = "dark__button">Top Rates</button>
                </div>

                <Row className = "g-1">
                    {ArraysOfProductOnMap}
                </Row>
            </Container>
        </div>
    )
}

export default Product
