import React from "react";
import './Helper.css';

function Helper(){

    const[number, setNumber] = React.useState(1);
    const[result, setResult] = React.useState(500)

    function OnChangeNumber(){
        setResult(number * 500)
    }

    return(
        <div>
            <h1>Счетчик: {result}</h1>
            <input value = {number} type = "number" onChange={OnChangeNumber} />
        </div>
    )
}

export default Helper
