import './Myriad.css';

import Mural from './Myriad-images/Locally_mural.png'

import { Col, Container, Row } from "react-bootstrap";

function Myriad(){

    const ArraysOfMyraid = [{
        title: 'Brand Strategy',
        image: Mural,
        text1: 'Locally stands apart because its robust product capabilities enable an easier and more connected online-to-offline local shopping experience for both businesses and consumers. Along with the company’s mission to democratize retail and disrupt e-commerce giants, we found none of these elements were expressed in its existing visual and verbal identity.',
        text2: "As Locally moved toward the consumer as its primary audience, it became increasingly important to create clarity around who Locally is for, and the difference in its brand’s offerings. So, we evaluated the company’s brand architecture — the intentional structure of products, services, and components that make up the brand's portfolio of offerings."
    }]
    const ArraysOfMyraidOnMap = ArraysOfMyraid.map((ArrayOfMyraid)=>
    <div>
        <img src = {ArrayOfMyraid.image} className='w-100'/>

        <h4>{ArrayOfMyraid.title}</h4>

        <Row>
            <Col lg = "6">{ArrayOfMyraid.text1}</Col>
            <Col lg = "6">{ArrayOfMyraid.text2}</Col>
        </Row>
    </div>
    )

    return(
        <Container fluid>
            <Container>
                <div className='myriad'>
                    <p className = "text">Locally’s myriad offerings — and evolving design and voice — meant the current brand was inconsistent and didn’t reflect its robust capabilities and powerful story.</p>
                    <img src = {Mural} className='w-100'/>
                </div>

                {ArraysOfMyraidOnMap}
            </Container>
        </Container>
    )
}

export default Myriad
