import './Product.css';

import Product1 from '../Image/product1.jpeg';
import Product2 from '../Image/product2.jpeg';
import Product3 from '../Image/product3.jpeg';

import { Col, Container, Row } from "react-bootstrap";

function Product(){

    const ArraysOfProduct = [{
        id: 1,
        image: Product1,
        title: "Now we're getting somewhere",
        subtitle: 'Megan Anderson & Victoria West',
        text: 'Sed ut iis bonis erigimur, quae expectamus, sic laetamur iis, quae recordamur. At hoc in eo M. Quid de Platone aut de Democrito loquar. Quae in controversiam veniunt, de iis, si placet, disseramus. Iam quae...',
        button: 'Product'
    },
    {
        id: 2,
        image: Product2,
        title: 'Design is a mirror, reflecting the culture',
        subtitle: 'Victoria West & David Cole',
        text: 'Quasi vero aut concedatur in omnibus stultis aeque magna esse vitia, et eadem inbecillitate et inconstantia L. Levatio igitur vitiorum magna fit in iis.',
        button: 'Interior'
    },
    {
        id: 3,
        image: Product3,
        title: 'Everything has beauty, but not everyone sees it',
        subtitle: 'Charles Barrett and others',
        text: 'Utilitatis causa amicitia est quaesita. Quae quo sunt excelsiores, eo dant clariora indicia naturae. Sed...',
        button: 'Story'
    }];
    const ArraysOfProductOnMap = ArraysOfProduct.map((ArrayOfProduct) =>
    <div key = {ArrayOfProduct.id} className = "product-element">
        <img src = {ArrayOfProduct.image} className = "w-100 h-100 product-image"/>
        <div className = "product-content">
            <h2 className = "product-title">{ArrayOfProduct.title}</h2>
            <h6 className = "product-subtitle">{ArrayOfProduct.subtitle}</h6>
            <p className = "product-text">{ArrayOfProduct.text}</p>
            <div><button className = "button">{ArrayOfProduct.button}</button></div>
        </div>
    </div> 
    )

    return(
        <Container fluid>
            <div className = "product-wrapper">
                <Row className = "g-1">
                    <Col lg = "6" md = "12" sm = "12">
                        {ArraysOfProductOnMap[0]}
                    </Col>
                    <Col lg = "3" md = "6" sm = "12">
                        {ArraysOfProductOnMap[1]}
                    </Col>
                    <Col lg = "3" md = "6" sm = "12">
                        {ArraysOfProductOnMap[2]}
                    </Col>
                </Row>
            </div>
        </Container>
    )
}

export default Product
