import './Recommendation.css';

import { Col, Container, Row } from "react-bootstrap";

import Photo1 from '../Recommendation/RecommendationImage/photo1.jpeg';
import Photo2 from '../Recommendation/RecommendationImage/photo2.jpeg';
import Photo3 from '../Recommendation/RecommendationImage/photo3.jpeg';
import Photo4 from '../Recommendation/RecommendationImage/photo4.jpeg';
import Photo5 from '../Recommendation/RecommendationImage/photo5.jpeg';
import Photo6 from '../Recommendation/RecommendationImage/photo6.jpeg';


function Recommendation(){

    const ArraysOfRecommendation = [{
        id: 1,
        image: Photo1,
        title: 'The trick to getting more done is to have the freedom to roam around'
    },
    {
        id: 2,
        image: Photo2,
        title: 'Every day, in every city and town across the country'
    },
    {
        id: 3,
        image: Photo3,
        title: 'I work best when my space is filled with inspiration'
    },
    {
        id: 4,
        image: Photo4,
        title: 'I have my own definition of minimalism'
    },
    {
        id: 5,
        image: Photo5,
        title: 'Change your look and your attitude'
    },
    {
        id: 6,
        image: Photo6,
        title: 'The difference is quality'
    }];
    const ArraysOfRecommendationOnMap = ArraysOfRecommendation.map((ArrayOfRecommendation) => 
    <Col key = {ArrayOfRecommendation.id} lg = "2" md = "4" sm = "6">
        <img src = {ArrayOfRecommendation.image} className = "w-100 recommend-image"/>

        <p className = "recommend-title">{ArrayOfRecommendation.title}</p>
    </Col>
    )

    return(
        <Container fluid>
            <hr/>
            <small className = "recommend-uptitle">
                Recommended
            </small>
            <Row>
                {ArraysOfRecommendationOnMap}
            </Row>
        </Container>
    )
}

export default Recommendation;
