import { Col, Container, Row } from "react-bootstrap";

import Photo1 from '../Posts/PostsImage/photo1.jpeg';
import Photo2 from '../Posts/PostsImage/photo2.jpg';
import Photo3 from '../Posts/PostsImage/photo3.jpg';

function Posts(){

    const ArraysOfPosts = [{
        id: 1,
        image: Photo1,
        text: "It's new era in design, there are no rules"
    },
    {
        id: 2,
        image: Photo2,
        text: 'The secret is to work less as individuals and more as a team'
    },
    {
        id: 3,
        image: Photo3,
        text: 'I have my own definition of minimalism'
    }];
    const ArraysOfPostsOnMap = ArraysOfPosts.map((ArrayOfPosts) =>
    <Col key = {ArrayOfPosts.id} lg = "4" md = "4" sm = "4">
        <div className = 'posts-card'>
            <img src = {ArrayOfPosts.image} className = "w-100"/>
            <p>{ArrayOfPosts.text}</p>
        </div>
    </Col>
    )

    return(
        <Container fluid>
            <div className = "post-wrapper">
                <div className = "text-center">
                    <h3>Editor's Choice</h3>
                    <h1>Get Started with our best Stories</h1>

                    <Row>
                        {ArraysOfPostsOnMap}
                    </Row>
                </div>
            </div>
        </Container>
    )
}

export default Posts
