import React from "react";
import './Helper.css';


function Helper(){
const [number, setNumber] = React.useState(0);
const [cost, setCost] = React.useState(0);

function OnIncrease(){
    setNumber(number + 1);
    setCost(cost + 500)
}

function OnDecrease(){
    setNumber(number - 1);
    setCost(cost - 500);
}
function TwoIncrease(){
    setNumber(number * 2);
    setCost(cost * 2)
}

    return(
        <div className="block">
            <div className="number">
                <input className="input" value = {number} /> <br/>
                Цена: {cost}тг
            </div>

            <div>
                <button onClick={OnIncrease} className = "btn btn-primary m-1">+</button>
                <button onClick={OnDecrease} className = "btn btn-danger m-1">-</button>
                <button onClick={TwoIncrease} className = "btn btn-info">*2</button>
            </div>
        </div>
    )
}

export default Helper
