import React from 'react';

import './ToDoList.css';

import { Button, Container, Form, InputGroup } from 'react-bootstrap'

function ToDoList(){

    const[InputClickText, SetInputClickText] = React.useState("")

    const AddInputClick = (event) =>{
        SetInputClickText(event.target.value)
    }

    return(
        <Container className = "text-center">
            <div className = "block">
                <div className='title'>
                    <h1>Title of ToDoList</h1>
                </div>

                <InputGroup>
                <InputGroup.Text>#</InputGroup.Text>
                <Form.Control placeholder='Add something to ToDoList' value = {InputClickText}></Form.Control>
                <Button onClick={AddInputClick} variant = "outline-secondary">Button</Button>
                <ul>
                    {todos.map((todo) => (
                    <li key={todo.id}>
                        {todo.text}
                        <button onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
                    </li>
                    ))}

                </ul>
                </InputGroup>
            </div>
        </Container>
    )
}

export default ToDoList
