import { Container } from 'react-bootstrap';
import './test.css';

function Test(){

    
    

    function ColorGenerator(){
        const hexCode = "0123456789ABCDEF";
        let Color = "";

        for(let i = 0; i < 6; i ++){
            Color += hexCode[Math.ceil(Math.random() * hexCode.length)] 
        }
        return "#" + Color
    }

    return(
        <Container fluid>
            <div className = "container">
                <h1>Сообщение: {ColorGenerator}</h1>
                <button className = "button" onClick={() => {
                    alert('Hi')
                }}>
                    Нажми на меня
                </button>
            </div>
        </Container>
    )
}

export default Test
