import './Category.css';

import { Col, Container, Row } from "react-bootstrap";

import Category1 from '../MorianImage/category1.webp';
import Category2 from '../MorianImage/category2.webp';
import Category3 from '../MorianImage/category3.webp';

function Category(){

    const ArraysOfCategory = [{
        id:1,
        image: Category1,
        title: 'Outdoor & Garden',
        link: ''
    },
    {
        id:2,
        image: Category2,
        title: 'Mirrors & Art',
        link: ''
    },
    {
        id:3,
        image: Category3,
        title: 'Office & Storage',
        link: ''
    }];
    const ArraysOfCategoryOnMap = ArraysOfCategory.map((ArrayOfCategory) => 
    <Col xl = "4" lg = "4" md = "4" sm = "6" key = {ArrayOfCategory.id}>
        <div className = "category__card">
            <img src = {ArrayOfCategory.image} className = "w-100 category__image"/>
        </div>
            
        <div className='category__block'>
            <a className='category__bottom' href = {ArrayOfCategory.link}>{ArrayOfCategory.title}</a>
        </div>
    </Col>
    )

    return(
        <div>
            <Container fluid>
                <div className = "category">
                    <h1 className = "category__title">Featured Categories</h1>
                    <div class = "line"></div>
                    <Row className = "g-2">
                        {ArraysOfCategoryOnMap}
                    </Row>
                </div>
            </Container>
        </div>
    )
}

export default Category
